// Get query parameters
const userId = $('Webhook').item.json.query?.userId;
const technicianId = $('Webhook').item.json.query?.technicianId;
const objectId = $('Webhook').item.json.query?.objectId;
const type = $('Webhook').item.json.query?.type;

// Get all transactions
let transactions = $input.all().map(item => item.json);

// Filter by userId/technicianId if provided
if (userId || technicianId) {
  const filterUserId = userId || technicianId;
  transactions = transactions.filter(t => 
    String(t.userId) === String(filterUserId)
  );
}

// Filter by objectId if provided
if (objectId) {
  transactions = transactions.filter(t => 
    String(t.objectId) === String(objectId)
  );
}

// Filter by type if provided
if (type) {
  transactions = transactions.filter(t => 
    t.type === type
  );
}

// Sort by date descending (newest first)
transactions.sort((a, b) => {
  const dateA = new Date(a.date || 0);
  const dateB = new Date(b.date || 0);
  return dateB - dateA;
});

return [{ json: { data: transactions } }];
