// Get the CSV content (text)
const body = $input.item.json.body;
const csvContent = body.csv;

if (!csvContent) {
  return [{ json: { success: false, error: 'No CSV content provided' } }];
}

// Return the CSV text for parsing
return [{ json: { csvContent } }];

// Parse Asset Bank CSV format
// Columns (separated by ;):
// 0: Сметка/Карта (IBAN)
// 1: Дата на транзакцията (DD.MM.YYYY)
// 2: Дб/Кр (Дебит/Кредит)
// 3: Сума
// 4: Валута
// 5-12: Bank details...
// 13: Тип операция (Description)
// etc.

const csvContent = $input.item.json.csvContent;
const lines = csvContent.split('\n');

const transactions = [];
const processedRefs = new Set(); // Track unique transactions

// Skip header row
for (let i = 1; i < lines.length; i++) {
  const line = lines[i].trim();
  if (!line) continue;

  const cols = line.split(';');
  if (cols.length < 14) continue;

  const iban = cols[0].trim();
  const dateStr = cols[1].trim();
  const typeRaw = cols[2].trim().toLowerCase();
  const amountStr = cols[3].trim();
  const currency = cols[4].trim();
  const description = cols[13].trim();
  const reference = cols[17] ? cols[17].trim() : '';

  // Skip if not main account (IBAN) or if it's a card number starting with 5318
  if (!iban.startsWith('BG')) continue;

  // Skip duplicates (same reference)
  if (reference && processedRefs.has(reference)) continue;
  if (reference) processedRefs.add(reference);

  // Parse date (DD.MM.YYYY -> YYYY-MM-DD)
  const dateParts = dateStr.split('.');
  const isoDate = dateParts.length === 3 
    ? `${dateParts[2]}-${dateParts[1]}-${dateParts[0]}`
    : dateStr;

  // Parse amount (remove spaces, handle comma as decimal)
  const cleanAmount = amountStr.replace(/\s/g, '').replace(',', '.');
  const amount = parseFloat(cleanAmount) || 0;

  // Determine type: Дебит = debit (expense), Кредит = credit (income)
  // Note: In Bulgarian bank statements, Дебит means money going OUT (expense)
  // and Кредит means money coming IN (income)
  const type = typeRaw.includes('дебит') || typeRaw.includes('debit') ? 'debit' : 'credit';

  transactions.push({
    date: isoDate,
    reference,
    description,
    type,
    amount,
    currency: currency || 'BGN',
    iban,
    status: 'unmatched'
  });
}

// Sort by date descending
transactions.sort((a, b) => b.date.localeCompare(a.date));

// Calculate totals
const totalDebit = transactions
  .filter(t => t.type === 'debit')
  .reduce((sum, t) => sum + t.amount, 0);

const totalCredit = transactions
  .filter(t => t.type === 'credit')
  .reduce((sum, t) => sum + t.amount, 0);

return [{ 
  json: { 
    success: true, 
    data: {
      transactions,
      count: transactions.length,
      totalDebit: Math.round(totalDebit * 100) / 100,
      totalCredit: Math.round(totalCredit * 100) / 100
    }
  } 
}];
