// Get existing transactions and new transactions from request
const existingItems = $input.all();
const body = $('Webhook').first().json.body;
const newTransactions = body.transactions || [];

if (!newTransactions.length) {
  return [{ json: { success: true, insertedCount: 0, duplicateCount: 0, message: 'No transactions provided' } }];
}

// Create set of existing references for quick lookup
const existingRefs = new Set();
let maxId = 0;

existingItems.forEach(item => {
  if (item.json.reference) {
    existingRefs.add(String(item.json.reference));
  }
  const id = parseInt(item.json.id) || 0;
  if (id > maxId) maxId = id;
});

// Filter out duplicates and assign IDs
const toInsert = [];
let duplicateCount = 0;
const importDate = new Date().toISOString().split('T')[0];

newTransactions.forEach(tx => {
  const ref = String(tx.reference || '');

  if (ref && existingRefs.has(ref)) {
    duplicateCount++;
  } else {
    maxId++;
    toInsert.push({
      id: maxId,
      reference: tx.reference || '',
      date: tx.date || '',
      type: tx.type || 'debit',
      amount: tx.amount || 0,
      currency: tx.currency || 'BGN',
      category: tx.category || 'other',
      description: tx.description || '',
      displayName: tx.displayName || '',
      counterpartyName: tx.counterpartyName || '',
      invoiceRef: tx.invoiceRef || '',
      purpose: tx.purpose || '',
      objectId: tx.objectId || '',
      objectName: tx.objectName || '',
      isCompanyExpense: tx.isCompanyExpense ? 'TRUE' : 'FALSE',
      status: tx.status || 'unmatched',
      importDate: importDate
    });
    // Add to set to prevent duplicates within same batch
    if (ref) existingRefs.add(ref);
  }
});

// Store counts for response
const insertCount = toInsert.length;

if (toInsert.length === 0) {
  return [{ json: { 
    success: true, 
    skip: true,
    insertedCount: 0, 
    duplicateCount: duplicateCount,
    message: `Всички ${duplicateCount} транзакции вече съществуват.`
  } }];
}

// Return transactions with metadata
return toInsert.map((tx, idx) => ({ 
  json: {
    ...tx,
    _meta: { insertCount, duplicateCount, isLast: idx === toInsert.length - 1 }
  }
}));

// Get the last item which has the counts
const items = $input.all();
const lastItem = items[items.length - 1];
const meta = lastItem.json._meta || { insertCount: items.length, duplicateCount: 0 };

return [{
  json: {
    success: true,
    message: `Записани ${meta.insertCount} транзакции. Пропуснати ${meta.duplicateCount} дубликати.`,
    insertedCount: meta.insertCount,
    duplicateCount: meta.duplicateCount
  }
}];
