// Get transactions from request body
const body = $input.item.json.body;
const transactions = body.transactions || [];

if (!transactions.length) {
  return [{ json: { success: false, error: 'No transactions provided' } }];
}

// Return transactions for processing
return transactions.map(tx => ({ json: tx }));

// Get existing transactions and new transactions
const existingItems = $('Get Existing Transactions').all();
const newItems = $('Prepare Transactions').all();

// Create set of existing references for quick lookup
const existingRefs = new Set();
let maxId = 0;

existingItems.forEach(item => {
  if (item.json.reference) {
    existingRefs.add(item.json.reference);
  }
  const id = parseInt(item.json.id) || 0;
  if (id > maxId) maxId = id;
});

// Filter out duplicates and assign IDs
const toInsert = [];
const duplicates = [];
const importDate = new Date().toISOString().split('T')[0];

newItems.forEach(item => {
  const tx = item.json;

  if (tx.reference && existingRefs.has(tx.reference)) {
    duplicates.push(tx.reference);
  } else {
    maxId++;
    toInsert.push({
      id: maxId,
      reference: tx.reference || '',
      date: tx.date || '',
      type: tx.type || 'debit',
      amount: tx.amount || 0,
      currency: tx.currency || 'BGN',
      category: tx.category || 'other',
      description: tx.description || '',
      displayName: tx.displayName || '',
      counterpartyName: tx.counterpartyName || '',
      invoiceRef: tx.invoiceRef || '',
      purpose: tx.purpose || '',
      objectId: tx.objectId || '',
      objectName: tx.objectName || '',
      isCompanyExpense: tx.isCompanyExpense ? 'TRUE' : 'FALSE',
      status: tx.status || 'unmatched',
      importDate: importDate
    });
  }
});

// Store results for response
$execution.customData.set('insertCount', toInsert.length);
$execution.customData.set('duplicateCount', duplicates.length);

if (toInsert.length === 0) {
  return [{ json: { skip: true, duplicateCount: duplicates.length } }];
}

return toInsert.map(tx => ({ json: tx }));

// Get counts from custom data
const insertCount = $execution.customData.get('insertCount') || 0;
const duplicateCount = $execution.customData.get('duplicateCount') || 0;

return [{
  json: {
    success: true,
    message: `Записани ${insertCount} транзакции. Пропуснати ${duplicateCount} дубликати.`,
    insertedCount: insertCount,
    duplicateCount: duplicateCount
  }
}];

// All were duplicates
const duplicateCount = $json.duplicateCount || 0;

return [{
  json: {
    success: true,
    message: `Всички ${duplicateCount} транзакции вече съществуват.`,
    insertedCount: 0,
    duplicateCount: duplicateCount
  }
}];
